scripts.text:
try {
    Write-Host 'Starting PowerShell script execution...'

    # Simple test first
    Write-Host 'Current Time:' (Get-Date)
    Write-Host 'Machine Name:' $env:COMPUTERNAME
    Write-Host 'PowerShell Version:' $PSVersionTable.PSVersion

    Write-Host 'PowerShell script execution completed successfully!'

} catch {
    Write-Error ('Script execution failed: {0}' -f $_.Exception.Message)
    Write-Host ('Error details: {0}' -f $_.Exception.ToString())
    throw
}
